mapper.update:
UPDATE claimQuestion
SET qcheck=#{qcheck}
WHERE qno=#{qno}

UPDATE claimAnswer
SET acontent=#{acontent}
WHERE qno=#{qno}

mapper.select:
SELECT * FROM claimQuestion WHERE 1=1
AND nickname=#{util.nickname}
AND qtitle LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY qno DESC LIMIT #{start}, #{cntPerPage}
AND qcontent LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY qno DESC LIMIT #{start}, #{cntPerPage}
AND qtitle LIKE CONCAT('%', #{util.keyword}, '%') OR qcontent LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY qno DESC LIMIT #{start}, #{cntPerPage}
ORDER BY qno DESC LIMIT 5
ORDER BY qno DESC LIMIT #{start}, #{cntPerPage}

SELECT * FROM reportboard WHERE 1=1
AND title LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY pno DESC LIMIT #{start}, #{cntPerPage}
AND rcontent LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY pno DESC LIMIT #{start}, #{cntPerPage}
AND title LIKE CONCAT('%', #{util.keyword}, '%') OR rcontent LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY pno DESC LIMIT #{start}, #{cntPerPage}
AND rtype LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY rdatetime DESC LIMIT #{start}, #{cntPerPage}
ORDER BY rdatetime DESC LIMIT #{start}, #{cntPerPage}

mapper.update:
UPDATE claimQuestion 
SET qtitle = #{qtitle}, qcontent = #{qcontent}
WHERE qno = #{qno}

mapper.delete:


	DELETE
	FROM claimAnswer
	WHERE ano = #{ano};


