mapper.insert:
INSERT INTO board (id, nickname, title, bcontent, bdatetime, views, likes, bno, commentCount)
VALUES(#{id}, #{nickname}, #{title}, #{bcontent}, sysdate(), 0, 0, #{bno}, 0);


	INSERT INTO filelist
	( pno, thumbnailName )
	SELECT pno, #{thumbnailName}
	FROM board
	ORDER BY pno DESC
	LIMIT 1;

mapper.update:
UPDATE board 
SET title = #{title}, bcontent = #{bcontent}
WHERE pno = #{pno};

	UPDATE filelist
	SET thumbnailName = #{thumbnailName}
	WHERE pno = #{pno};

mapper.delete:
DELETE FROM board
WHERE pno = #{pno};

DELETE FROM commentList
WHERE pno = #{pno};


	DELETE FROM filelist
	WHERE pno = #{pno};

mapper.select:
SELECT * FROM board
WHERE id=#{id}
ORDER BY pno DESC

select count(*)
from board
where id=#{id}

select count(*)
from commentlist
where id=#{id}

select *
from board
where id=#{util.id}

and title like CONCAT('%', #{util.boardKeyword}, '%')


and bcontent like CONCAT('%', #{util.boardKeyword}, '%')

limit #{start}, #{cntPerPage}

mapper.delete:
delete

from board
where pno=#{no}


from commentlist
where cno=#{no}

mapper.select:
select count(*)
from board
where bno = #{bno}

	AND id=#{id}

SELECT *
FROM board as b
WHERE bno=#{util.bno}


		AND id=#{util.id}



		AND title LIKE CONCAT('%', #{util.keyword}, '%')
		ORDER BY pno DESC
		LIMIT #{start}, #{cntPerPage}



		AND bcontent LIKE CONCAT('%', #{util.keyword}, '%')
		ORDER BY pno DESC
		LIMIT #{start}, #{cntPerPage}



		AND title LIKE CONCAT('%', #{util.keyword}, '%')
		OR bcontent LIKE CONCAT('%', #{util.keyword}, '%')
		ORDER BY pno DESC
		LIMIT #{start}, #{cntPerPage}



		AND nickname LIKE CONCAT('%', #{util.keyword}, '%')
		ORDER BY pno DESC
		LIMIT #{start}, #{cntPerPage}



		ORDER BY pno DESC
		LIMIT #{start}, #{cntPerPage}



		ORDER BY likes DESC, pno DESC
		LIMIT #{start}, #{cntPerPage}



		ORDER BY likes DESC
		LIMIT 5;



		ORDER BY pno DESC
		LIMIT 5;

SELECT * FROM board as b JOIN filelist as f ON b.pno = f.pno
AND title LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY f.pno DESC LIMIT #{start}, #{cntPerPage}
AND b.bcontent LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY f.pno DESC LIMIT #{start}, #{cntPerPage}
AND b.title LIKE CONCAT('%', #{util.keyword}, '%') OR b.bcontent LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY f.pno DESC LIMIT #{start}, #{cntPerPage}
AND b.nickname LIKE CONCAT('%', #{util.keyword}, '%') ORDER BY f.pno DESC LIMIT #{start}, #{cntPerPage}
ORDER BY f.pno DESC LIMIT #{start}, #{cntPerPage}
ORDER BY b.likes DESC, f.pno DESC LIMIT #{start}, #{cntPerPage}
ORDER BY b.likes DESC
ORDER BY f.pno DESC

mapper.update:
UPDATE board SET
commentCount = commentCount + 1
WHERE pno = #{pno}

UPDATE board SET
views = views + 1
WHERE pno = #{pno}

mapper.select:

SELECT count(*) FROM likelist
WHERE id = #{id}
AND pno = #{pno}

mapper.update:
update board
set likes = likes+1
where pno = #{pno}

update board
set likes = likes-1
where pno = #{pno}

mapper.select:

SELECT pno, title 
FROM(
		(SELECT pno, title
		FROM board
		WHERE pno < #{pno}
		ORDER BY pno DESC LIMIT 1) UNION ALL
		(SELECT pno, title
		FROM board
		WHERE pno > #{pno}
		LIMIT 1) 
	)AS title;

mapper.update:
UPDATE board SET
commentCount= commentCount-1
WHERE pno=#{pno}

mapper.select:
SELECT COUNT(*) 
FROM reportBoard
WHERE pno = #{pno}
AND nickname = #{nickname}
