mapper.select:
SELECT * FROM board WHERE bno = 1 AND (title LIKE CONCAT('%',#{title},'%') OR bcontent like CONCAT('%',#{title},'%'))
ORDER BY bdatetime DESC LIMIT 5;
ORDER BY likes DESC LIMIT 5;
ORDER BY bdatetime DESC
ORDER BY likes DESC

SELECT * FROM board WHERE bno = 2 AND (title LIKE CONCAT('%',#{title},'%') OR bcontent like CONCAT('%',#{title},'%'))
ORDER BY bdatetime DESC LIMIT 5;
ORDER BY likes DESC LIMIT 5;
ORDER BY bdatetime DESC
ORDER BY likes DESC
